package fecJavClas;

public class PositiveOrNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int s = 90;
		int y = -80;
		if (s > 0 && y <0) {
			System.out.println("Positive");
		} else if (s == 0) {
			System.out.println("zero");
		}

		else {
			System.out.println("Negative");
		}

	}

}
